import React, { useState } from "react";

import TalkOrderPage from "./TalkOrderPage";
import TalkProductPage from "./TalkProductPage";
import TalkTalkPage from "./TalkTalkPage";

import TitleBasic from "../../../components/man/as/TitleBasic";

import ManTalkImg from "../../assets/man/talk_navy.png";

function TalkPage() {
  const [selectedButton, setSelectedButton] = useState(1);

  // 버튼 클릭 시 해당 컨텐츠를 표시하는 함수
  const handleButtonClick = (content) => {
    setSelectedButton(content); // 선택된 버튼 업데이트
  };
  return (
    <div className="alltalk">
      <div className="toptalk">
        <TitleBasic imgSrc={ManTalkImg} title="견적 및 문의서 관리" />
      </div>

      <div className="middletalk">
        <div className="content-container-talk">
          {/* 버튼 영역 */}
          <div className="button-container-talk">
            {/* 견적요청서 버튼 */}
            <button
              className={`button-each ${selectedButton === 1 ? "active" : ""}`}
              onClick={() => handleButtonClick(1)}
            >
              견적 요청서
            </button>
            {/* 제품 사용 문의 버튼 */}
            <button
              className={`button-each ${selectedButton === 2 ? "active" : ""}`}
              onClick={() => handleButtonClick(2)}
            >
              제품 사용 문의
            </button>
            {/* 상담 문의 버튼 */}
            <button
              className={`button-each ${selectedButton === 3 ? "active" : ""}`}
              onClick={() => handleButtonClick(3)}
            >
              상담 문의
            </button>
          </div>
          {/* 선택된 컨텐츠 표시 */}
          <div className="content">
            {selectedButton === 1 && <TalkOrderPage />}
            {selectedButton === 2 && <TalkProductPage />}
            {selectedButton === 3 && <TalkTalkPage />}
          </div>
        </div>
      </div>

      <div className="bottomtalk"></div>
    </div>
  );
}

export default TalkPage;
