import React from "react";
import styles from "./TableAs.module.css";

// Mock 데이터
const mockData = [
  {
    id: 1,
    작성일: "2024-04-07",
    업체명: "ABC 정육점",
    전화번호: "123-456-7890",
    수리상품: "다이서 MCD-380N",
    수리유형: "고장",
    상태: "요청 대기",
  },
  {
    id: 2,
    작성일: "2024-04-06",
    업체명: "XYZ 푸드",
    전화번호: "987-654-3210",
    수리상품: "탕파 절단기 SVS-1335",
    수리유형: "오작동",
    상태: "요청 수락",
  },
  {
    id: 3,
    작성일: "2024-04-06",
    업체명: "임도현",
    전화번호: "987-654-3210",
    수리상품: "골절기 SB-2275N",
    수리유형: "오작동",
    상태: "요청 대기",
  },
  {
    id: 4,
    작성일: "2024-04-04",
    업체명: "D mall",
    전화번호: "987-654-3210",
    수리상품: "무 탈피기 DAP-120",
    수리유형: "오작동",
    상태: "처리 완료",
  },
  {
    id: 5,
    작성일: "2024-04-04",
    업체명: "D mall",
    전화번호: "987-654-3210",
    수리상품: "무 탈피기 DAP-120",
    수리유형: "오작동",
    상태: "처리 완료",
  },
];

// 테이블 구성 코드
const TableAs = () => {
  return (
    <table>
      <thead>
        <tr>
          <th>작성일</th>
          <th>업체명</th>
          <th>전화번호</th>
          <th>수리상품</th>
          <th>수리요청</th>
          <th>상태</th>
        </tr>
      </thead>
      <tbody>
        {mockData.map((item) => (
          <tr key={item.id}>
            <td>{item.작성일}</td>
            <td>{item.업체명}</td>
            <td>{item.전화번호}</td>
            <td>{item.수리상품}</td>
            <td>{item.수리유형}</td>
            <td
              className={
                item.상태 === "요청 대기"
                  ? styles.pending
                  : item.상태 === "요청 수락"
                  ? styles.accepted
                  : ""
              }
            >
              {item.상태}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default TableAs;
