import React, { useState } from "react";
import mockData from "./mock_sample.json";
import "./ManCalllistPage.css"; /* 해당 패이지 css */
import "./ManCallmisPage.js"; /*부재중 페이지 import*/
import "./ManCallPage.js"; /* 통화 목록 버튼 페이지 */

function Man_CalllistPage() {
  const [selectedButton, setSelectedButton] = useState(null);
  const [rowData, setRowData] = useState(null);

  // 통화 목록 버튼 클릭 시 데이터 추출
  const handleButtonClick = () => {
    setSelectedButton(1); // 버튼 스타일 변경

    // JSON 데이터에서 필요한 정보 추출
    const extractedData = mockData.map(
      ({ name, date, time, cluster, keyword }) => ({
        name,
        date,
        time,
        cluster,
        keyword: keyword.join(" "),
      })
    );

    setRowData(extractedData); // 추출된 데이터 설정
  };

  return (
    <div className="container">
      <h1 className="title">통화</h1>
      <div className="button-container">
        {/* 버튼들에 대한 클릭 이벤트 처리 */}
        <button
          className={`button-each ${selectedButton === 1 ? "active" : ""}`}
          onClick={handleButtonClick} // 클릭 핸들러 연결
        >
          통화 목록
        </button>
        <button
          className={`button-each ${selectedButton === 2 ? "active" : ""}`}
          onClick={() => setSelectedButton(2)}
        >
          부재중 조회
        </button>
        <button
          className={`button-each ${selectedButton === 3 ? "active" : ""}`}
          onClick={() => setSelectedButton(3)}
        >
          키워드 조회
        </button>
      </div>
      {/* 데이터가 존재하면 출력 */}
      {rowData && (
        <div>
          <table>
            <thead>
              <tr>
                <th>이름</th>
                <th>날짜</th>
                <th>시간</th>
                <th>분류</th>
                <th>키워드</th>
              </tr>
            </thead>
            <tbody>
              {rowData.map((data, index) => (
                <tr key={index}>
                  <td>{data.name}</td>
                  <td>{data.date}</td>
                  <td>{data.time}</td>
                  <td>{data.cluster}</td>
                  <td>{data.keyword}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Man_CalllistPage;
