import React, { useState } from "react";
import mockData from "./mock_sample.json";
import "./ManCalllistPage.css"; // CSS 파일 import

function ManCalllistPage() {
  const [selectedButton, setSelectedButton] = useState(null); // 선택된 버튼 상태 저장
  const [rowData, setRowData] = useState(null); // 데이터 상태 저장

  // 통화 목록 버튼 클릭 시 실행되는 함수
  const handleCallListClick = () => {
    setSelectedButton("callList"); // 통화 목록 버튼 선택 상태로 변경
    extractCallListData(); // 통화 목록 데이터 추출 함수 호출
  };

  // 통화 목록 데이터 추출 함수
  const extractCallListData = () => {
    const extractedData = mockData.map(
      ({ name, date, time, cluster, keyword }) => ({
        name,
        date,
        time,
        cluster,
        keyword: keyword.join(", "), // 키워드 배열을 문자열로 변환하여 띄어쓰기로 구분
      })
    );
    setRowData(extractedData); // 추출된 데이터 상태 업데이트
  };

  return (
    <div className="container">
      <h1 className="title">통화</h1>
      <div className="button-container">
        {/* 각 버튼에 대한 클릭 이벤트 처리 및 선택된 상태에 따른 스타일 지정 */}
        <button
          className={`button-each ${
            selectedButton === "callList" ? "active" : ""
          }`} // 선택된 버튼에 대한 스타일 적용
          onClick={handleCallListClick} // 통화 목록 버튼 클릭 시 실행되는 함수 연결
        >
          통화 목록
        </button>
        <button className="button-each">부재중 조회</button>
        <button className="button-each">고객 조회</button>
        <button className="button-each">키워드 조회</button>
      </div>
      {/* 데이터가 존재하면 출력 */}
      {rowData && (
        <div>
          <h2>추출된 데이터</h2>
          <table>
            <thead>
              <tr>
                <th>이름</th>
                <th>날짜</th>
                <th>시간</th>
                <th>분류</th>
                <th>키워드</th>
              </tr>
            </thead>
            <tbody>
              {rowData.map((data, index) => (
                <tr key={index}>
                  <td>{data.name}</td>
                  <td>{data.date}</td>
                  <td>{data.time}</td>
                  <td>{data.cluster}</td>
                  <td>{data.keyword}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default ManCalllistPage;
