import React, { useCallback, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import styles from "./UserMenu.module.css";
import useStoredUserInfo from "../../../lib/localStorage";
import Logout from "../../../pages/Logout";

const UserMenu = ({ Icon }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [userInfo, setUserInfo] = useState(null);

  const handleButtonClick = useCallback((e) => {
    e.stopPropagation();
    setIsOpen((nextIsOpen) => !nextIsOpen);
  }, []);

  useEffect(() => {
    // useEffect 내부에서 useStoredUserInfo를 호출하여 상태를 설정합니다.
    const userInfo = useStoredUserInfo();
    setUserInfo(userInfo);
    console.log("usermenu", userInfo);
  }, []); // 빈 배열을 전달하여 이 효과가 한 번만 실행되도록 합니다.

  useEffect(() => {
    if (!isOpen) return;

    const handleClickOutside = () => setIsOpen(false);
    window.addEventListener("click", handleClickOutside);

    return () => {
      window.removeEventListener("click", handleClickOutside);
    };
  }, [isOpen]);

  return (
    <div className={styles.userMenu}>
      <button className={styles.iconButton} onClick={handleButtonClick}>
        <img src={Icon} alt="사용자 메뉴" />
      </button>
      {isOpen && (
        <ul className={styles.popup}>
          {userInfo ? (
            <>
              <Link to="./mypage">
                <li>마이페이지</li>
              </Link>
              <li>
                <Logout />
              </li>{" "}
              {/* 로그아웃 버튼*/}
            </>
          ) : (
            <Link to="/login">
              <li>로그인</li>
            </Link>
          )}
        </ul>
      )}
    </div>
  );
};

export default UserMenu;
