import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import mockData from "../a_mock_sample.json";
import "./CallScriptPage.css"; // 스타일 파일을 import합니다.

function CallScriptPage() {
  const { id } = useParams(); // URL에서 id를 가져옵니다.
  const [originalData, setOriginalData] = useState(null);

  // 해당 id에 해당하는 전화 데이터를 찾습니다.
  useEffect(() => {
    const callData = mockData.find((call) => call.id === parseInt(id));
    if (callData) {
      // 원본 데이터를 설정합니다.
      setOriginalData(callData.original);
    }
  }, [id]);

  if (!originalData) {
    return <div>원본 데이터를 찾을 수 없습니다.</div>;
  }

  return (
    <div className="message-container">
      <h1 className="title">통화 내용</h1>
      {/* 원본 데이터를 출력합니다. */}
      <div className="message-wrapper">
        {originalData.map((message, index) => (
          <div
            key={index}
            className={`message-wrapper ${
              message.spk === 0 ? "spk-0" : "spk-1"
            }`}
          >
            {message.msg}
          </div>
        ))}
      </div>
      {/* 뒤로 가기 링크 */}
      <Link to={`/manager/calllist/call/${id}`} className="button-script">
        뒤로가기
      </Link>
    </div>
  );
}

export default CallScriptPage;
