import "./ManCalllistPage.css";
import React, { useState, useEffect } from "react";
import mockData from "./mock_sample.json";

function Man_CalllistPage() {
  const [selectedButton, setSelectedButton] = useState(null);
  const [dataToShow, setDataToShow] = useState([]);

  useEffect(() => {
    // 선택된 버튼에 따라 데이터를 필터링하여 표시
    if (selectedButton === 1) {
      setDataToShow(mockData); // 전체 데이터 표시
    } else if (selectedButton === 2) {
      setDataToShow(mockData.filter((item) => item.class === "부재중")); // class가 "부재중"인 데이터만 표시
    } else if (selectedButton === 3) {
      setDataToShow(mockData.filter((item) => item.class === "고객 조회")); // class가 "고객 조회"인 데이터만 표시
    } else if (selectedButton === 4) {
      setDataToShow(mockData.filter((item) => item.class === "키워드 조회")); // class가 "키워드 조회"인 데이터만 표시
    } else {
      setDataToShow([]); // 선택된 버튼이 없는 경우 데이터를 비웁니다.
    }
  }, [selectedButton]);

  return (
    <div className="container">
      <h1 className="title">통화</h1>
      <div className="button-container">
        {/* 버튼들에 대한 클릭 이벤트 처리 */}
        <button
          className={`button-each ${selectedButton === 1 ? "active" : ""}`}
          onClick={() => setSelectedButton(1)}
        >
          통화 전체 목록
        </button>
        <button
          className={`button-each ${selectedButton === 2 ? "active" : ""}`}
          onClick={() => setSelectedButton(2)}
        >
          부재중 조회
        </button>
        <button
          className={`button-each ${selectedButton === 3 ? "active" : ""}`}
          onClick={() => setSelectedButton(3)}
        >
          고객 조회
        </button>
        <button
          className={`button-each ${selectedButton === 4 ? "active" : ""}`}
          onClick={() => setSelectedButton(4)}
        >
          키워드 조회
        </button>
      </div>
      {/* 데이터를 표시하는 부분 */}
      <div className="data-container">
        {dataToShow.map((item, index) => (
          <div key={index} className="data-item">
            <p>이름: {item.name}</p>
            <p>전화한 날짜: {item.date}</p>
            <p>전화한 시간: {item.time}</p>
            <p>전화 텍스트 분류: {item.class}</p>
            <p>중요한 단어: {item.keyword.join(", ")}</p>
            <p>전화 내용 요약: {item.summarize}</p>
            <p>실제 전화 텍스트: {item.original}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Man_CalllistPage;
