import { useState, useEffect } from "react";

const localStorage = () => {
  // 사용자 정보를 상태로 관리
  const [userInfo, setUserInfo] = useState({});

  useEffect(() => {
    // localStorage에서 사용자 정보를 가져와서 상태로 설정
    const storedUserInfo = localStorage.getItem("customerInfo");
    if (storedUserInfo) {
      const { email, id, name } = JSON.parse(storedUserInfo).value;
      setUserInfo({ email, id, name });
    }
  }, []);

  return userInfo;
};

export default localStorage;
