import React, { useState } from "react";
import "./CusPage.module.css";

import mockDataCus from "../../components/man/cus/mockCus.json";

import TitleBasic from "../../components/man/TitleBasic";
import TableCus from "../../components/man/cus/TableCus";

import ManCusImg from "../../assets/man/cus_navy.png";

const CusPage = () => {
  //검색
  const [searchTerm, setSearchTerm] = useState("");
  const [customers, setCustomers] = useState([]);

  useEffect(() => {
    // JSON 파일을 가져와서 모의 데이터로 사용
    fetch("/path/to/mockCustomers.json")
      .then((response) => response.json())
      .then((data) => setCustomers(data))
      .catch((error) => console.error("Error fetching mock data:", error));
  }, []);

  // 검색 결과 필터링 함수
  const searchFilter = (customer) => {
    return (
      customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      customer.phone.includes(searchTerm)
    );
  };

  return (
    <div className="allcus">
      <div className="topcus">
        <TitleBasic imgSrc={ManCusImg} title="고객 정보 조회,관리" />
      </div>
      <div className="bottomcus">
        <div className="bottomtopus">
          <input
            type="text"
            placeholder="이름 또는 전화번호 검색"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="bottombottomus">
          <Table customers={customers.filter(searchFilter)} />{" "}
        </div>
      </div>
    </div>
  );
};

export default CusPage;
