// CusDetailsPage.js

import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import styles from "./CusDetailsPage.module.css";

import TitleSub from "../../../components/man/TitleSub";
import InfoCus from "../../../components/man/cus/InfoCus";
import TableCallList from "../../../components/man/call/TableCallList";

import ManCusImg from "../../../assets/man/cus_navy.png";
import mockDataCus from "../../../mockdata/mockCus.json";
import mockCallData from "../../../mockdata/a_mock_sample.json";

const CusDetailsPage = () => {
  const { id } = useParams();
  const [customerDetails, setCustomerDetails] = useState(null);
  const [matchingCalls, setMatchingCalls] = useState([]);

  useEffect(() => {
    const selectedCustomer = mockDataCus.find(
      (customer) => customer.id === parseInt(id)
    );

    setCustomerDetails(selectedCustomer);

    // mockCallData에서 이름과 전화번호가 일치하는 고객의 전화 내역 가져오기
    const calls = mockCallData.filter((call) => {
      const customer = mockDataCus.find(
        (customer) =>
          customer.phone === call.phone && customer.name === call.name
      );
      return customer && customer.id === parseInt(id);
    });

    setMatchingCalls(calls);
  }, [id]);

  if (!customerDetails) {
    return <div className={styles.allcusone}>세부 정보가 없습니다..</div>;
  }

  return (
    <div className={styles.allcusone}>
      <div className={styles.titlecusone}>
        <TitleSub imgSrc={ManCusImg} title="고객 상세페이지" />
      </div>

      <div className={styles.topcusone}>
        <InfoCus
          name={customerDetails.name}
          customerId={customerDetails.id}
          email={customerDetails.email}
          phone={customerDetails.phone}
        />
      </div>

      <div className={styles.middlecusone}>
        <h2 className={styles.middlecusoneTitle}>통화 목록</h2>
        {matchingCalls.length > 0 ? (
          <TableCallList
            rowData={matchingCalls.map((call) => ({
              id: call.id,
              date: call.date,
              time: call.time,
              cluster: call.cluster,
              keyword: call.keyword,
            }))}
          />
        ) : (
          <p>통화 내역이 없습니다!</p>
        )}
      </div>

      <div className={styles.bottomcusone}></div>
    </div>
  );
};

export default CusDetailsPage;
