import React, { useState } from "react";
import "./CusPage.module.css";

import TitleBasic from "../../components/man/TitleBasic";
import TableCus from "../../components/man/cus/TableCus";

import ManCusImg from "../../assets/man/cus_navy.png";

const CusPage = () => {
  //검색
  const [searchTerm, setSearchTerm] = useState("");

  // 검색 결과 필터링 함수
  const searchFilter = (customer) => {
    return (
      customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      customer.phone.includes(searchTerm)
    );
  };

  return (
    <div className="allcus">
      <div className="topcus">
        <TitleBasic imgSrc={ManCusImg} title="고객 정보 조회,관리" />
      </div>
      <div className="bottomcus">
        <div className="bottomtopus">
          <input
            type="text"
            placeholder="이름 또는 전화번호 검색"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <div className="bottombottomus">
          <TableCus />
        </div>
      </div>
    </div>
  );
};

export default CusPage;
