import React, { useState } from "react";
import "./ManCalllistPage.css"; /* 해당 페이지의 CSS 파일 */
import ManCallPage from "./ManCallPage.js"; /* 통화 목록 버튼 페이지 */
import ManKeywordPage from "./ManKeywordPage.js"; /* 키워드 페이지 */
import ManCallmissPage from "./ManCallmissPage.js"; /* 부재중 페이지 */

function Man_CalllistPage() {
  const [selectedButton, setSelectedButton] = useState(null);
  const [selectedContent, setSelectedContent] = useState(null);

  // 버튼 클릭 시 해당 컨텐츠를 표시하는 함수
  const handleButtonClick = (content) => {
    setSelectedButton(content); // 선택된 버튼 업데이트
    setSelectedContent(content); // 선택된 컨텐츠 업데이트
  };

  return (
    <div className="container">
      <h1 className="title">통화</h1>
      <div className="button-container">
        {/* 통화 목록 버튼 */}
        <button
          className={`button-each ${selectedButton === 1 ? "active" : ""}`}
          onClick={() => handleButtonClick(1)} // 클릭 이벤트 핸들러에 컨텐츠 전달
        >
          통화 목록
        </button>
        {/* 부재중 조회 버튼 */}
        <button
          className={`button-each ${selectedButton === 2 ? "active" : ""}`}
          onClick={() => handleButtonClick(2)}
        >
          부재중 조회
        </button>
        {/* 키워드 조회 버튼 */}
        <button
          className={`button-each ${selectedButton === 3 ? "active" : ""}`}
          onClick={() => handleButtonClick(3)}
        >
          키워드 조회
        </button>
      </div>
      {/* 선택된 컨텐츠 표시 */}
      <div className="content-container">
        {selectedContent === 1 && <ManCallPage />}
        {selectedContent === 2 && <ManCallmissPage />}
        {selectedContent === 3 && <ManKeywordPage />}
      </div>
    </div>
  );
}

export default Man_CalllistPage;
