import React from "react";
import styles from "./Form.module.css";

function Form({ title, fields, onSubmit }) {
  // submitDescription prop 추가
  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    let isValid = true;

    // 필수 필드 검사
    fields.forEach((field) => {
      if (field.required && !formData.get(field.name)) {
        isValid = false; // 필수 필드가 비어 있으면 유효하지 않음
        alert(`${field.label}는 필수 입력 사항입니다.`);
      }
    });

    if (isValid) {
      onSubmit(formData); // 모든 필수 입력이 유효한 경우에만 onSubmit 호출
    }
  };

  return (
    <form className={styles.form} onSubmit={handleSubmit}>
      <h1 className={styles.title}>{title}</h1>
      {fields.map((field, index) => (
        <div className={styles.field} key={index}>
          <label htmlFor={field.id} className={styles.label}>
            {field.label}
            {field.required && <span className={styles.requiredStar}>*</span>}
          </label>
          {field.type === "textarea" ? (
            <textarea
              className={styles.textarea}
              id={field.id}
              name={field.name}
              required={field.required}
            ></textarea>
          ) : (
            <input
              className={styles.input}
              type={field.type}
              id={field.id}
              name={field.name}
              required={field.required}
            />
          )}
        </div>
      ))}
      {submitDescription && (
        <p className={styles.submitDescription}>"확인 후, 연락처"</p>
      )}
      <button className={styles.submitButton} type="submit">
        제출하기
      </button>
    </form>
  );
}

export default Form;
