import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoadingPage = () => {
  const navigate = useNavigate();
  const [customerInfo, setCustomerInfo] = useState(null);

  const params = new URLSearchParams(window.location.search);
  const code = params.get("code");
  const state = params.get("state");

  const handleLoginPost = async (code, state) => {
    try {
      const res = await axios.get(
        `http://34.64.53.159:8000/api/v1/user/naver/callback?code=${code}&state=${state}`
      );
      console.log(res);
      console.log(res.data.user);
      setCustomerInfo(res.data.user);
      // Save user info to localStorage with a 2-hour expiration
      const expirationTime = new Date().getTime() + 2 * 60 * 60 * 1000; // 2 hours
      localStorage.setItem(
        "customerInfo",
        JSON.stringify({ user: res.data.user, expiresAt: expirationTime })
      );
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    if (code) {
      handleLoginPost(code, state);
    } else {
      console.log("로그인 코드가 없습니다.");
    }
  }, [code, state, navigate]);

  // Check localStorage for user info on component mount
  useEffect(() => {
    const storedUserInfo = localStorage.getItem("customerInfo");
    if (storedUserInfo) {
      const { user, expiresAt } = JSON.parse(storedUserInfo);
      if (expiresAt && expiresAt > new Date().getTime()) {
        setCustomerInfo(user);
      } else {
        // Clear expired user info from localStorage
        localStorage.removeItem("customerInfo");
      }
    }
  }, []);

  return (
    <div>
      <h2>Loading...</h2>
      {customerInfo && (
        <div>
          <h3>고객 이름</h3>
          <div>{customerInfo.name}</div>
          <h3>이메일</h3>
          <div>{customerInfo.email}</div>
        </div>
      )}
    </div>
  );
};

export default LoadingPage;
