import React, { useState, useEffect } from "react";
import axiosInstance from "../../../lib/axios";
import TableCallList from "../call/TableCallList"; // TableCallList 컴포넌트 import
import styles from "./CallCus.module.css";

import CusCall from "../../../assets/man/CusCall_navy.png";
import CusEmail from "../../../assets/man/CusEmail_navy.png";

const CallCus = ({ name, customerId, email, phone }) => {
  const [callList, setCallList] = useState([]);

  useEffect(() => {
    const fetchCallListData = async () => {
      try {
        const response = await axiosInstance.get("/api/v1/call");
        setCallList(response.data.data);
      } catch (error) {
        console.error("Error fetching call list data:", error);
      }
    };

    fetchCallListData();
  }, []);

  // 전화번호로 필터링된 통화 목록 가져오기
  const filteredCallList = callList.filter(
    (call) => call.phoneNumber === phone
  );

  return (
    <div className={styles.cusInfo}>
      <div className={styles.cusInfoTop}>
        <div className={styles.cusInfoName}>{name}고객님</div>
        <div className={styles.cusInfoId}>{customerId}</div>
      </div>

      {filteredCallList.length > 0 && (
        <div className={styles.cusInfoBottom}>
          <TableCallList rowData={filteredCallList} />
        </div>
      )}

      <div className={styles.cusInfoBottom}>
        <img src={CusCall} alt="전화번호" className={styles.icon} />
        <div className={styles.contents}>{phone}</div>
      </div>

      <div className={styles.cusInfoBottom}>
        <img src={CusEmail} alt="이메일" className={styles.icon} />
        <div className={styles.contents}>{email}</div>
      </div>
    </div>
  );
};

export default CallCus;
