import React from "react";
import { useParams, Link } from "react-router-dom";
import mockData from "./mock_sample.json";
import "./ManCallDetailsPage.css";

function ManCallDetailsPage() {
  const { id } = useParams(); // URL에서 id를 가져옵니다.

  // 해당 id에 해당하는 전화 데이터를 찾습니다.
  const callData = mockData.find((call) => call.id === parseInt(id));

  if (!callData) {
    return <div>해당 전화를 찾을 수 없습니다.</div>;
  }

  const { name, date, time, cluster, keyword, summarize } = callData;

  return (
    <div className="container-detail">
      <div className="content-detail">
        <h1 className="title-detail">{name}</h1>
        <div className="details">
          <p>
            <strong>날짜:</strong> {date}
          </p>
          <p>
            <strong>시간:</strong> {time}
          </p>
          <p>
            <strong>분류:</strong> {cluster}
          </p>
          <p>
            <strong>키워드:</strong> {keyword.join(", ")}
          </p>
          <p>
            <strong>요약:</strong> {summarize}
          </p>
        </div>
      </div>
      <div className="buttons">
        <Link to="/manager/calllist" className="button">
          뒤로가기
        </Link>
        <Link
          to={`/manager/calllist/call/${callData.id}/script`}
          className="button"
        >
          전체 스크립트 조회하기
        </Link>
      </div>
    </div>
  );
}

export default ManCallDetailsPage;
