import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import styles from "./TableCallList.module.css";

function parseDateAndTime(dateStr, timeStr) {
  // 날짜 "YYYY년 MM월 DD일"을 "YYYY-MM-DD"로 변환
  const dateParts = dateStr
    .split("년")
    .join("-")
    .split("월")
    .join("-")
    .split("일")
    .trim();

  // 시간 "HH시 MM분 SS초"을 "HH:MM:SS"로 변환
  const timeParts = timeStr
    .split("시")
    .join(":")
    .split("분")
    .join(":")
    .split("초")
    .trim();

  // 완성된 날짜와 시간 문자열
  return new Date(`${dateParts} ${timeParts}`);
}

function TableCallList({ rowData }) {
  const [sortedData, setSortedData] = useState([]);

  useEffect(() => {
    if (rowData) {
      const sorted = [...rowData].sort((a, b) => {
        // 날짜와 시간을 파싱하여 Date 객체 생성
        const dateA = parseDateAndTime(a.date, a.time);
        const dateB = parseDateAndTime(b.date, b.time);
        return dateB - dateA; // 내림차순 정렬
      });
      setSortedData(sorted);
    }
  }, [rowData]);

  return (
    <div>
      {sortedData && (
        <div>
          <table className={styles.callTable}>
            <thead>
              <tr>
                <th>이름</th>
                <th>날짜</th>
                <th>시간</th>
                <th>분류</th>
                <th>키워드</th>
                <th>더보기</th>
              </tr>
            </thead>
            <tbody>
              {sortedData.map((data) => (
                <tr key={data.id}>
                  <td>{data.name}</td>
                  <td>{data.date}</td>
                  <td>{data.time}</td>
                  <td>{data.cluster}</td>
                  <td>
                    <div className={styles.keywordContainer}>
                      {data.keyword &&
                        data.keyword.map((keyword, index) => (
                          <div key={index} className={styles.keywordBox}>
                            {keyword}
                          </div>
                        ))}
                    </div>
                  </td>
                  <td>
                    <Link
                      to={`/manager/calllist/call/${data.id}`}
                      className={styles.linkMore}
                    >
                      더보기
                    </Link>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default TableCallList;
