import React from "react";
import Form from "../../components/cus/Form";

function OrderPage() {
  const formFields = [
    { id: "name", name: "name", label: "이름", type: "text", required: true },
    {
      id: "contact",
      name: "contact",
      label: "연락처",
      type: "text",
      required: true,
    },
    {
      id: "email",
      name: "email",
      label: "이메일",
      type: "email",
      required: true,
    },
    {
      id: "product",
      name: "product",
      label: "상품명",
      type: "text",
      required: true,
    },
    {
      id: "quantity",
      name: "quantity",
      label: "상품 수",
      type: "number",
      required: true,
    },
    {
      id: "address",
      name: "address",
      label: "주소",
      type: "text",
      required: true,
    },
    {
      id: "additional",
      name: "additional",
      label: "기타 요청사항",
      type: "textarea",
      required: false,
    },
  ];

  const handleSubmit = (event) => {
    event.preventDefault();
    const formData = new FormData(event.target);
    const data = {};
    formData.forEach((value, key) => (data[key] = value));
    console.log(data); // 여기서 서버로 데이터를 보낼 수 있습니다.
  };

  return (
    <Form title="견적서 작성" fields={formFields} onSubmit={handleSubmit} />
  );
}

export default OrderPage;
