import React, { useState } from "react";
import styles from "./TalkTable.module.css";

const mockData = [
  {
    id: 1,
    작성일: "2024-04-07",
    업체명: "ABC 정육점",
    전화번호: "123-456-7890",
    이메일: "abc@example.com",
    주문상품: "다이서 MCD-380N",
    상태: "요청 수락",
  },
  // 나머지 mockData
];

const TableOrder = () => {
  const [data, setData] = useState(mockData);

  const handleStatusChange = (id) => {
    const newData = data.map((item) => {
      if (item.id === id) {
        if (item.상태 === "요청 대기") {
          return { ...item, 상태: "요청 수락" };
        } else if (item.상태 === "요청 수락") {
          return { ...item, 상태: "처리 완료" };
        }
      }
      return item;
    });
    setData(newData);
  };

  return (
    <table className={styles.OrderTable}>
      <thead>
        <tr>
          <th>작성일</th>
          <th>업체명</th>
          <th>전화번호</th>
          <th>이메일</th>
          <th>주문상품</th>
          <th>상태</th>
          <th>동작</th> {/* 새로운 열 추가 */}
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item.id}>
            <td>{item.작성일}</td>
            <td>{item.업체명}</td>
            <td>{item.전화번호}</td>
            <td>{item.이메일}</td>
            <td>{item.주문상품}</td>
            <td
              className={
                item.상태 === "요청 대기"
                  ? styles.waiting
                  : item.상태 === "처리 완료"
                  ? styles.completed
                  : ""
              }
            >
              {item.상태}
            </td>
            <td>
              {item.상태 === "요청 대기" && (
                <button onClick={() => handleStatusChange(item.id)}>
                  요청 수락
                </button>
              )}
              {item.상태 === "요청 수락" && (
                <button onClick={() => handleStatusChange(item.id)}>
                  처리 완료
                </button>
              )}
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default TableOrder;
