import React from "react";
import { useParams, Link } from "react-router-dom";
import mockData from "./mock_sample.json";
import "./ManCallDetailsPage.css";

function ManCallDetailsPage() {
  const { id } = useParams(); // URL에서 id를 가져옵니다.

  // 해당 id에 해당하는 전화 데이터를 찾습니다.
  const callData = mockData.find((call) => call.id === parseInt(id));

  if (!callData) {
    return <div>해당 전화를 찾을 수 없습니다.</div>;
  }

  const { name, date, time, cluster, keyword, summarize } = callData;

  return (
    <div className="container">
      <h1>전화 상세 정보</h1>
      <h2 className="title">{name}</h2>
      <h3>날짜: {date}</h3>
      <p>시간: {time}</p>
      <p>분류: {cluster}</p>
      <p>키워드: {keyword.join(", ")}</p>
      <p>요약: {summarize}</p>
      <Link to="/manager/calllist">뒤로가기</Link>
    </div>
  );
}

export default ManCallDetailsPage;
