import React, { useState } from "react";

function ASRepairRequest() {
  const [step, setStep] = useState(1);
  const [partNumber, setPartNumber] = useState("");
  const [productInfo, setProductInfo] = useState({});
  const [repairType, setRepairType] = useState("");

  const handlePartNumberSubmit = () => {
    // API 호출로 제품 정보 검색
    // 예시를 위해 하드코드된 데이터 사용
    setProductInfo({ name: "제품명", model: "모델명" });
    setStep(2);
  };

  const handleRepairTypeSubmit = () => {
    setStep(3);
  };

  const handleFinalSubmit = () => {
    // 모든 정보를 API로 전송
    console.log({ partNumber, productInfo, repairType });
    // 요청 완료 후 추가 작업 (예: 상태 초기화, 알림 메시지 등)
  };

  return (
    <div>
      <h1>A/S 수리 요청</h1>
      {step === 1 && (
        <div>
          <label>
            상품 품번 입력:
            <input
              type="text"
              value={partNumber}
              onChange={(e) => setPartNumber(e.target.value)}
            />
          </label>
          <button onClick={handlePartNumberSubmit}>다음</button>
        </div>
      )}
      {step === 2 && (
        <div>
          <p>제품명: {productInfo.name}</p>
          <p>모델명: {productInfo.model}</p>
          <label>
            수리 유형 선택:
            <select
              value={repairType}
              onChange={(e) => setRepairType(e.target.value)}
            >
              <option value="">선택하세요</option>
              <option value="hardware">기계 멈춤 현상</option>
              <option value="software">부품 마모/손상</option>
              <option value="other">기계 오작동</option>
              <option value="other">기타</option>
            </select>
          </label>
          <button onClick={handleRepairTypeSubmit}>다음</button>
        </div>
      )}
      {step === 3 && (
        <div>
          <button onClick={handleFinalSubmit}>A/S 수리 요청서 작성</button>
        </div>
      )}
    </div>
  );
}

export default ASRepairRequest;
