import React, { useEffect, useState } from "react";
import NaverLogin from "./NaverLogin";

const UserInfo = () => {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const getUser = async () => {
      await NaverLogin.getLoginStatus((status) => {
        console.log(`로그인?:${status}`);
        if (status) {
          setUser({ ...NaverLogin.user });
        }
      });
    };

    const initNaverLogin = async () => {
      await NaverLogin.init();
      console.log("init!");
      getUser();
    };

    initNaverLogin();
  }, []);

  // const naverLogout = () => {
  //   // 네이버 로그아웃 함수를 구현해야 함
  // };

  return (
    <div>
      {user ? (
        <div>
          <h2>네이버 로그인 성공!</h2>
          <h3>사용자 이름</h3>
          <div>{user.name}</div>
          <h3>사용자 이메일</h3>
          <div>{user.email}</div>
          <h3>사용자 프로필사진</h3>
          <img src={user.profile_image} alt="프로필 사진" />
        </div>
      ) : null}
    </div>
  );
};

export default UserInfo;
